import { Component, OnInit } from '@angular/core';

interface Pessoa {
  id: number;
  nome: string;
  idade: number;
}

@Component({
  selector: 'app-lista-pessoas',
  templateUrl: './lista-pessoas.component.html',
  styleUrls: ['./lista-pessoas.component.css']
})
export class ListaPessoasComponent implements OnInit {
  pessoas: Pessoa[] = [
    { id: 1, nome: 'João', idade: 4 },
    { id: 2, nome: 'Maria', idade: 6 },
    { id: 3, nome: 'Pedro', idade: 3 }
  ];

  constructor() { }

  ngOnInit(): void {
  }
}
<!-- lista-pessoas.component.html -->

<table>
  <thead>
    <tr>
      <th>ID</th>
      <th>Nome</th>
      <th>Idade</th>
      <th>Editar</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let pessoa of pessoas">
      <td>{{ pessoa.id }}</td>
      <td>{{ pessoa.nome }}</td>
      <td>{{ pessoa.idade }}</td>
      <td *ngIf="pessoa.idade < 5">
        <button [routerLink]="['/admin/pessoas/editar', pessoa.id]">Editar</button>
      </td>
    </tr>
  </tbody>
</table>
