import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Pessoa } from '../pessoa.model'; 
@Component({
  selector: 'app-pessoas',
  templateUrl: './lista-pessoas.component.html',
  styleUrls: ['./lista-pessoas.component.scss'],
})
export class PessoasComponent {
  pessoas: Pessoa[] = [ 
    new Pessoa(1, 'Pessoa 1', 2),
    new Pessoa(2, 'Pessoa 2', 4),
  ];

  mostrarApenasMaioresDe30: boolean = false;

  pessoaForm: FormGroup;
  pessoaSelecionada: Pessoa | null = null;

  constructor(private formBuilder: FormBuilder) {
    this.pessoaForm = this.formBuilder.group({
      nome: '',
      idade: 0,
    });
  }

  adicionarPessoa() {
    const novaPessoa = this.pessoaForm.value;
    const id = this.pessoas.length + 1; // Gere um ID incremental
    this.pessoas.push(new Pessoa(id, novaPessoa.nome, novaPessoa.idade)); // Adicione a nova pessoa com ID
    this.pessoaForm.reset();
  }

  verDetalhes(pessoa: Pessoa) {
    this.pessoaSelecionada = pessoa;
  }
}

<table>
  <thead>
    <tr>
      <th>ID</th>
      <th>Nome</th>
      <th>Idade</th>
      <th>Editar</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let pessoa of pessoas">
      <td>{{ pessoa.id }}</td>
      <td>{{ pessoa.nome }}</td>
      <td>{{ pessoa.idade }}</td>
      <td *ngIf="pessoa.idade < 5">
        <button [routerLink]="['/admin/pessoas/editar', pessoa.id]">Editar</button>
      </td>
    </tr>
  </tbody>
</table>
